var ALL_DAYS_LIST = [2270][13]int{} // 这里得到一个总数

func init() {
	// 目前精度只能 30年到2300年
	// 需要在这里计算出每一年每一月的距离原点的天数
	// 公元30年1月1日是第 10645天
	var nTotalDays int = 10644
	for nYear := 30; nYear < 2300; nYear++ {
		for nMonth := 1; nMonth <= 13; nMonth++ {
			// 就要把天数写到对应的内容里去
			ALL_DAYS_LIST[nYear-30][nMonth-1] = nTotalDays
			// 天数叠加
			nTotalDays += GetMonthDays(nYear, nMonth)
		}
	}

	init2()
}

